import { useState } from "react";
import "../StyleCSS/Headerbox.css";
import { Outlet, Link } from "react-router-dom";
import { useAuth } from "./AuthContext";  // Import the useAuth hook

function Headerbox() {
  const [tab, setTab] = useState("login");
  const { user, setUser } = useAuth();  // Get user state and setUser function

  // Handle Logout function
  const handleLogout = () => {
    console.log("Logging out...");  // Debugging log
    setUser(null);  // Clear the user context
    setTab("logout");  // Switch to login tab after logout
  };

  return (
    <div>
      <div className="headerbox">
        <Link
          to="/"
          className="headerboxcomponent logo"
          onClick={() => tab === "logging" ? setTab("logout") : ""}
        >
          MovieManagement
        </Link>
        {logRender(tab, setTab, user, setUser, handleLogout)}  {/* Pass handleLogout properly */}
      </div>
      <Outlet />
    </div>
  );
}

function Logging({ setTab, setUser }) {
  const [inputs, setInputs] = useState({
    username: '',
    password: '',
  });

  const eventHandler = (event) => {
    const { name, value } = event.target;
    setInputs(prevInputs => ({
      ...prevInputs,
      [name]: value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    fetch(`http://localhost:3001/api/checklogin?username=${inputs.username}&password=${inputs.password}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.data && data.data.length > 0) {
          // Login success, update AuthContext
          setUser(data.data);  // Assuming data.data contains user info
          setTab("login");  // Set tab to logged-in state
          alert("Login successful!");
        } else {
          alert("Invalid username or password");
        }
      })
      .catch((error) => console.error("Error fetching login:", error));

    setInputs({
      username: '',
      password: '',
    });
  };

  return (
    <div>
      <div className="Login-tab">
        <form className="login-block" onSubmit={handleSubmit}>
          <div className="login-exit-to-logout" onClick={() => setTab("logout")}>X</div>
          <label>Username</label>
          <input
            type="text"
            name="username"
            value={inputs.username}
            onChange={eventHandler}
          />
          <label>Password</label>
          <input
            type="password"
            name="password"
            value={inputs.password}
            onChange={eventHandler}
          />
          <input className="login-submit" type="submit" value={"Sign In"} />
        </form>
      </div>
    </div>
  );
}

function logRender(tab, setTab, user, setUser, handleLogout) {
  console.log(`Rendering tab: ${tab}`);  // Debugging log
  switch (tab) {
    case "logout":
      return logOut(handleLogout);
    case "logging":
      return <Logging setTab={setTab} setUser={setUser} />;
    case "login":
      return logIn(user, handleLogout);
    default:
      return null;
  }
}

function logOut(handleLogout) {
  console.log("In logout view");  // Debugging log
  return (
    <div className="header-links">
      <Link to="/films" className="headerboxcomponent">Films</Link>
      <Link to="/members" className="headerboxcomponent">Members</Link>
      <div className="headerboxcomponent" onClick={handleLogout}>Logout</div>  {/* Ensure the function is being triggered */}
      <Link to="/signup" className="headerboxcomponent">Signup</Link>
    </div>
  );
}

function logIn(user, handleLogout) {
  return (
    <div className="header-links">
      <Link to="/memberonly" className="headerboxcomponent">{user ? user.username : 'Insert Name'}</Link>
      <Link to="/films" className="headerboxcomponent">Films</Link>
      <Link to="/members" className="headerboxcomponent">Members</Link>
      <div className="headerboxcomponent" onClick={handleLogout}>Logout</div>  {/* Handle logout here too */}
    </div>
  );
}

export default Headerbox;
