import React, { createContext, useState, useContext } from 'react';

// Create the AuthContext
const AuthContext = createContext();

// Create a custom hook to use AuthContext
export const useAuth = () => {
  return useContext(AuthContext);
};

// AuthProvider to wrap the components that need access to the context
export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null); // The user will be null when logged out

  // Function to log in the user
  const login = (username, password) => {
    // Simulate API call to check credentials
    // Replace this with your real login API call
    if (username === 'test' && password === 'password') {
      setUser({ username }); // Simulate a successful login
    } else {
      console.log('Invalid credentials');
    }
  };

  // Function to log out the user
  const logout = () => {
    setUser(null); // Reset the user state to null
  };

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
