import '../StyleCSS/Reviews.css';

function Reviews() {
  const reviews = [
    {
      moviePicture: "path/to/movie1.jpg",
      movieName: "Movie 1",
      reviewText: "A very intriguing movie with a compelling storyline.",
      rating: 4.5,
      reviewDate: "2024-11-20",
    },
    {
      moviePicture: "path/to/movie2.jpg",
      movieName: "Movie 2",
      reviewText: "Amazing cinematography and great performances.",
      rating: 5.0,
      reviewDate: "2024-11-18",
    },
    // Add more reviews as needed
  ];

  return (
    <div className='review-page'>
      <div className="review-page-container">
        <div className='review-page-heading'>Reviews of this person</div>
        <div className='review-page-selectorbox'>
          <div className='review-page-selectorbox__element'>
            <p>Rating<i className="arrow down"></i></p>
          </div>
          <div className='review-page-selectorbox__element'>
            <p>Year<i className="arrow down"></i></p>
          </div>
          <div className='review-page-selectorbox__element'>
            <p>When Reviewed<i className="arrow down"></i></p>
          </div>
        </div>
        <div className='review-page-entries'>
          {reviews.map((review, index) => (
            <div className='review' key={index}>
              <img src={review.moviePicture} alt={review.movieName} className='review__image' />
              <div className='review__details'>
                <h3 className='review__movie-name'>{review.movieName}</h3>
                <p className='review__text'>{review.reviewText}</p>
                <p className='review__rating'>Rating: {review.rating}</p>
                <p className='review__date'>Reviewed on: {review.reviewDate}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Reviews;
