import { useState, useEffect, useRef } from 'react';
import '../StyleCSS/Filmonly.css'
import { useParams } from 'react-router-dom';
import { useFilm } from './FilmContext.js';

function Filmonly() {
  const isMounted = useRef(true); // Flag to track mount status

  useEffect(() => {
    return () => {
      isMounted.current = false; // Set to false when the component unmounts
    };
  }, []);

  const { selectedFilm } = useFilm();
  const [choice_desc, setChoice_desc] = useState('actors');
  const [genres, setGenres] = useState([]);
  const [actors, setActors] = useState([]);
  const [directors, setDirectors] = useState([]);

  useEffect(() => {
    fetchDescriptionOption('http://localhost:3001/api/filmpagegenres?', `id=${selectedFilm[0]}`, setGenres);
    fetchDescriptionOption('http://localhost:3001/api/filmpageactors?', `id=${selectedFilm[0]}`, setActors);
    fetchDescriptionOption('http://localhost:3001/api/filmpagedirectors?', `id=${selectedFilm[0]}`, setDirectors);
  }, [selectedFilm]);

  function fetchDescriptionOption(get_api, critera, setArray) {
    let customApi = get_api + "" + critera;
    fetch(customApi, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then((data) => {
        setArray(data.data);
      })
      .catch((error) => console.error("Error fetching search results:", error));
  }

  return (
    <div className='film-container'>
      <div className='film-header'>
        <h1 className='film-title'>{selectedFilm[1]}</h1>
        <p className='film-year'>{new Date(selectedFilm[2]).getFullYear()}</p>
        <p className='film-director-label'>Directed by</p>
        <p className='film-director'>{directors[0]}</p>
      </div>

      <div className='film-main'>
        {/* Left Side: Picture */}
        <div className='film-image'>
          <img src={selectedFilm[0]} alt={selectedFilm[1]} />
        </div>

        {/* Middle: Description, Cast, Genres, Directors */}
        <div className='film-details'>
          <div className='film-description'>{selectedFilm[3]}</div>
          <div className='film-categories'>
            <button className={`category-btn ${choice_desc === 'actors' ? 'active' : ''}`} onClick={() => setChoice_desc('actors')}>Cast</button>
            <button className={`category-btn ${choice_desc === 'directors' ? 'active' : ''}`} onClick={() => setChoice_desc('directors')}>Directors</button>
            <button className={`category-btn ${choice_desc === 'genres' ? 'active' : ''}`} onClick={() => setChoice_desc('genres')}>Genres</button>
          </div>
          <div className="film-info">
            {currentDesc({ directors, actors, genres }, choice_desc)}
          </div>
        </div>

        {/* Right Side: Rating, Log Options, Buttons */}
        <div className='film-actions'>
          <div className='film-rating'>
            <h3>Rating</h3>
            <div className='rating-score'>{selectedFilm[4]}</div>
          </div>
          <div className='film-buttons'>
            <button className="action-btn">Add to Watched</button>
            <button className="action-btn">Add to Watchlist</button>
            <button className="action-btn">Add to Favorites</button>
            <button className="action-btn">Add a Review</button>
            <button className="action-btn">Add to Lists</button>
          </div>
        </div>
      </div>
    </div>
  )
}

function currentDesc(arrays, choice_desc) {
  return (
    <div className="description-list">
      {arrays[choice_desc]?.map((item, index) => (
        <div key={index} className='description-item'>{item}</div>
      ))}
    </div>
  )
}

export default Filmonly;
