function Headerbox() {
  const [tab, setTab] = useState("logout");
  
  // Call the hooks outside of the conditional rendering
  const renderContent = logRender(tab, setTab);
  
  return (
    <div>
      <div className="headerbox">
        <Link
          to="/"
          className="headerboxcomponent logo"
          onClick={() => tab === "logging" ? setTab("logout") : ""}
        >
          MovieManagement
        </Link>
        {renderContent}
      </div>
      <Outlet />
    </div>
  );
}

function logRender(tab, setTab) {
  // Ensure hooks are not called conditionally
  const commonProps = { setTab }; // You can pass common props to all components

  if (tab === "logout") {
    return LogOut(commonProps);
  } else if (tab === "logging") {
    return <Logging {...commonProps} />;
  } else if (tab === "login") {
    return LogIn(commonProps);
  }
  return null;
}

function LogIn({ setTab }) {
  const { setSelectedMember } = useSelectedMember();
  const { user } = useAuth();
  const navigate = useNavigate(); // Always called here, not conditionally

  const handleSelectMemberOnly = (userId) => {
    setSelectedMember({ userId });
    navigate('/memberonly');
    console.log(userId);
  };

  return (
    <div className="header-links">
      <Link to={"/memberonly"} onClick={() => handleSelectMemberOnly(user.userId)} className="headerboxcomponent">
        {user.username}  
      </Link>
      <Link to="/films" className="headerboxcomponent">Films</Link>
      <Link to="/members" className="headerboxcomponent">Members</Link>
      <div className="headerboxcomponent" onClick={() => setTab("logout")}>Logout</div>
    </div>
  );
}
