import React, { useState } from "react";
import "../StyleCSS/Headerbox.css";
import { Outlet, Link } from "react-router-dom";
import { useAuth } from "../PagesJS/AuthContext.js";  // Correct import for useAuth

function Headerbox() {
  const [tab, setTab] = useState("login"); // Manage the current tab
  const { user, setUser } = useAuth(); // Use AuthContext

  // Handle Logout function
  const handleLogout = () => {
    setUser(null); // Clear the user context
    setTab("login"); // Switch to the login tab after logout
  };

  return (
    <div>
      <div className="headerbox">
        <Link
          to="/"
          className="headerboxcomponent logo"
          onClick={() => tab === "logging" ? setTab("logout") : ""}
        >
          MovieManagement
        </Link>
        {logRender(tab, setTab, user, setUser, handleLogout)}  {/* Pass handleLogout properly */}
      </div>
      <Outlet />
    </div>
  );
}

// Other components (Logging, logRender, etc.) remain the same

export default Headerbox;
