import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useAuth } from "./AuthContext"; // Import AuthContext
import "../StyleCSS/Headerbox.css";

function Headerbox() {
  const { user, login, logout } = useAuth(); // Get user info and login/logout functions from context
  const [tab, setTab] = useState(user ? "login" : "logout"); // Set the tab based on login state

  // handleSubmit function to handle the login process
  const handleSubmit = (event, username, password) => {
    event.preventDefault();
    login(username, password); // Check login credentials using the context

    if (username && password) {
      setTab("login"); // Switch to login tab if successful
    }
  };

  return (
    <div>
      <div className="headerbox">
        <Link
          to="/"
          className="headerboxcomponent logo"
          onClick={() => tab === "logging" ? setTab("logout") : ""}
        >
          MovieManagement
        </Link>
        {user ? logIn(tab, setTab, logout) : logOut(tab, setTab)}
        logRender(tab,)
      </div>
    </div>
  );
}

function logRender(tab, setTab, logout) {
  if (tab === "logout") return logOut(tab, setTab);
  else if (tab === "logging") return Logging(tab, setTab, handleSubmit);
  else if (tab === "login") return logIn(tab, setTab, logout);
}

function logOut(tab, setTab) {
  return (
    <div className="header-links">
      <Link to="/films" className="headerboxcomponent">Films</Link>
      <Link to="/members" className="headerboxcomponent">Members</Link>
      <div className="headerboxcomponent" onClick={() => setTab("logging")}>
        Login
      </div>
      <Link to="/signup" className="headerboxcomponent">Signup</Link>
    </div>
  );
}

function Logging(tab, setTab, handleSubmit) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  return (
    <div>
      <div className="Login-tab">
        <form
          className="login-block"
          onSubmit={(e) => handleSubmit(e, username, password)} // Pass the handleSubmit function here
        >
          <div className="login-exit-to-logout" onClick={() => setTab("logout")}>
            X
          </div>
          <label>Username</label>
          <input
            type="text"
            name="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <label>Password</label>
          <input
            type="password"
            name="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <input className="login-submit" type="submit" value={"Sign In"} />
        </form>
      </div>
    </div>
  );
}

function logIn(tab, setTab, logout) {
  return (
    <div className="header-links">
      <Link to="/memberonly" className="headerboxcomponent">
        insertName
      </Link>
      <Link to="/films" className="headerboxcomponent">Films</Link>
      <Link to="/members" className="headerboxcomponent">Members</Link>
      <div
        className="headerboxcomponent"
        onClick={() => {
          setTab("logout");
          logout(); // Logout the user when they click logout
        }}
      >
        Logout
      </div>
    </div>
  );
}

export default Headerbox;
